# Parse HTML
html_content += h2+h3+h4
soup = BeautifulSoup(html_content, 'html.parser')

# Find all <td> elements that contain repo names
repo_cells = soup.find_all('td', class_="sorting_1")

# Extract repository names and owners
repos = []
for cell in repo_cells:
    repo_info = cell.find_next('td').text.strip()  # Get the repository name from next <td>
    owner, repo_name = repo_info.split('/')
    repos.append({'owner': owner, 'repo_name': repo_name})

# Output the results
for repo in repos:
    print(f"{x}Owner: {repo['owner']}, Repo Name: {repo['repo_name']}")

